import React, { Component } from 'react'

import Service from '../../service/Auth.service'

class SignupForm extends Component {

    constructor(props) {
        super(props)
        this._service = new Service()
        this.state = { username: '', password: '' }
    }

    handleSubmit = e => {
        e.preventDefault()
        const { username, password } = this.state
        this._service.signup(username, password)
            .then(theNewUser => {
                this.props.setUser(theNewUser.data)
                this.setState({ username: '', password: '' })
                       // REDIRECCIONAMIENTO
            })
            .catch(err => console.log(err.response.data.message))
    }


    handleInputChange = e => {
        let { name, value } = e.target
        this.setState({ [name]: value })
    }

    render() {
        return (
            <>

                <h1>Registro</h1>

                <form onSubmit={this.handleSubmit}>
                    <div>
                        <label>Usuario</label>
                        <input type="text" name="username" onChange={this.handleInputChange} value={this.state.username} />
                    </div>
                    <div>
                        <label>Contrase√±a</label>
                        <input type="text" name="password" onChange={this.handleInputChange} value={this.state.password} />
                    </div>
                    <button variant="dark" type="submit">Registrarme</button>
                </form>

            </>
        )
    }
}


export default SignupForm
